SELECT * FROM (SELECT PER_ID, PER_Libelle, true as 'Boolean' FROM ICA_Permission JOIN ICA_ROLE_HAS_PERMISSION USING (PER_ID) JOIN ICA_Role USING (ROL_ID) WHERE ROL_ID = ? UNION SELECT PER_ID, PER_Libelle, false as 'Boolean' FROM ICA_Permission WHERE PER_ID NOT IN ( SELECT PER_ID FROM ICA_Permission JOIN ICA_ROLE_HAS_PERMISSION USING (PER_ID) JOIN ICA_Role USING (ROL_ID) WHERE ROL_ID = ? )) as T1 ORDER BY PER_ID